import{_ as e,c as t,ag as l,o as i}from"./chunks/framework.CmpABV1Y.js";const p=JSON.parse('{"title":"Data Availability in Evolve","description":"","frontmatter":{"head":[["meta",{"name":"og:title","content":"Data Availability in Evolve | Evolve"},{"name":"og:description","content":false}]]},"headers":[],"relativePath":"learn/data-availability.md","filePath":"learn/data-availability.md","lastUpdated":1755869279000}'),r={name:"learn/data-availability.md"};function o(n,a,s,d,c,v){return i(),t("div",null,a[0]||(a[0]=[l('<h1 id="data-availability-in-evolve" tabindex="-1">Data Availability in Evolve <a class="header-anchor" href="#data-availability-in-evolve" aria-label="Permalink to &quot;Data Availability in Evolve&quot;">​</a></h1><p>Data availability (DA) is a core of Evolve&#39;s. Evolve utilize&#39;s data availability ensures that all transaction data and block information required to verify the chain&#39;s state is accessible to anyone running a node or light client.</p><p>Learn more about data availability:</p><ul><li><a href="https://celestia.org/what-is-da/" target="_blank" rel="noreferrer">What is DA</a></li><li><a href="https://medium.com/zeeve/exploring-data-availability-layer-and-its-importance-in-rollups-0a4fbf2e0ffc" target="_blank" rel="noreferrer">The importance of DA for Rollups</a></li></ul><h2 id="how-evolve-handles-data-availability" tabindex="-1">How Evolve Handles Data Availability <a class="header-anchor" href="#how-evolve-handles-data-availability" aria-label="Permalink to &quot;How Evolve Handles Data Availability&quot;">​</a></h2><p>Evolve is designed to be DA-agnostic, meaning it can integrate with different data availability layers depending on your needs. The main options are:</p><ul><li><p><strong>Local Data Availability (Local DA):</strong></p><ul><li>Used for development, testing, and local deployments.</li><li>Not secure for production, as data can be withheld by the node operator.</li></ul></li><li><p><strong>External Data Availability Layer (DA Interface):</strong></p><ul><li>Used for production and secure deployments.</li><li>Evolve can post block data to any external DA layer that implements the Evolve <a href="https://github.com/evstack/ev-node/blob/main/core/da/da.go#L11" target="_blank" rel="noreferrer">DA interface</a> (e.g., Celestia).</li><li>Anyone can verify that the data is available and reconstruct the chain state, depending on the guarantees of the chosen DA layer.</li></ul></li></ul><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><ul><li><strong>Use Local DA only for development and testing locally.</strong></li><li><strong>Alternatively, you can use <a href="https://docs.celestia.org/how-to-guides/participate" target="_blank" rel="noreferrer">Celestia testnets</a>.</strong></li><li><strong>For production, always use a decentralized DA layer that implements the Evolve DA interface.</strong></li></ul><h2 id="learn-more" tabindex="-1">Learn More <a class="header-anchor" href="#learn-more" aria-label="Permalink to &quot;Learn More&quot;">​</a></h2><ul><li><a href="./guides/da/local-da">Set up a local DA</a></li><li><a href="./guides/da/celestia-da">Set up Celestia DA</a></li><li><a href="https://docs.celestia.org/" target="_blank" rel="noreferrer">Celestia Docs</a></li></ul>',11)]))}const u=e(r,[["render",o]]);export{p as __pageData,u as default};
